def checkPalidroma(s):
    for i in xrange(len(s)//2+1):
        if s[i]!=s[len(s)-i-1]:
            return False
    return True

def longestPalidroma(s):
    max=len(s)
    while max>=1:
        for x in xrange(len(s)-max+1):
            if checkPalidroma(s[x:x+max]):
                return s[x:x+max]
        max-=1
    return max

